mapper.select:
SELECT id, article_id, user_id, create_time, update_time, deleted
FROM bg_article_like
WHERE article_id = #{articleId}
  AND user_id = #{userId}
LIMIT 1

mapper.update:
UPDATE bg_article_like
SET deleted = 1,
    update_time = NOW()
WHERE article_id = #{articleId}
  AND user_id = #{userId}

UPDATE bg_article_like
SET deleted = #{deleted},
    update_time = NOW()
WHERE article_id = #{articleId}
  AND user_id = #{userId}
